(function () {
	$(function () {

		var _roleService = abp.services.app.role;
		var _$modal = $('#RoleCreateModal');
		var _$form = _$modal.find('form');

		_$form.validate({
		});

		$('#RefreshButton').click(function () {
			refreshRoleList();
		});

		$('.delete-role').click(function () {
			var roleId = $(this).attr("data-role-id");
			var roleName = $(this).attr('data-role-name');

			deleteRole(roleId, roleName);
		});

		$('.edit-role').click(function (e) {
			debugger;
			var roleId = $(this).attr("data-role-id");

			e.preventDefault();
			// abp.ajax({
			// url: abp.appPath + 'Roles/EditRoleModaljson?roleId=' + roleId,
			// type: 'POST',
			// contentType: 'application/Html',
			// success: function (content) {
			// debugger;
			// alert(content);
			// //$('#RoleEditModal div.modal-content').html(content);
			// },

			// error: function (e)
			// {

			// debugger;

			// //alert(e.responseText);
			// $('#RoleEditModal div.modal-content').html(e.responseText);
			// }
			// });


			$.ajax({
				url: abp.appPath + 'Roles/EditRoleModal?roleId=' + roleId,
				type: 'post',
				dataType: 'json',
				contentType: 'application/json;charset=utf-8',
				success: function (response) {
					alert("heloo");
					$('#RoleEditModal div.modal-content').html(response);
				},
				error: function (error) {
					Debugger;
					$('#RoleEditModal div.modal-content').html(error.responseText);
					alert(error.statusText);
					// alert(error.statusText);
				}
			});

		});

		_$form.find('button[type="submit"]').click(function (e) {
			e.preventDefault();

			if (!_$form.valid()) {
				return;
			}

			var role = _$form.serializeFormToObject(); //serializeFormToObject is defined in main.js
			role.grantedPermissions = [];
			var _$permissionCheckboxes = $("input[name='permission']:checked");
			if (_$permissionCheckboxes) {
				for (var permissionIndex = 0; permissionIndex < _$permissionCheckboxes.length; permissionIndex++) {
					var _$permissionCheckbox = $(_$permissionCheckboxes[permissionIndex]);
					role.grantedPermissions.push(_$permissionCheckbox.val());
				}
			}

			abp.ui.setBusy(_$modal);
			_roleService.create(role).done(function () {
				_$modal.modal('hide');
				location.reload(true); //reload page to see new role!
			}).always(function () {
				abp.ui.clearBusy(_$modal);
			});
		});

		_$modal.on('shown.bs.modal', function () {
			_$modal.find('input:not([type=hidden]):first').focus();
		});

		function refreshRoleList() {
			location.reload(true); //reload page to see new role!
		}

		function deleteRole(roleId, roleName) {
			abp.message.confirm(
				abp.utils.formatString(abp.localization.localize('AreYouSureWantToDelete', 'TFCLPortal'), roleName),
				function (isConfirmed) {
					if (isConfirmed) {
						_roleService.delete({
							id: roleId
						}).done(function () {
							refreshRoleList();
						});
					}
				}
			);
		}
	});
})();
